import './SearchCity.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faLocationDot, faMagnifyingGlass } from '@fortawesome/free-solid-svg-icons';
import fontawesome from '@fortawesome/fontawesome'

import useWeatherService from '../../services/WeatherService';

fontawesome.library.add(faLocationDot, faMagnifyingGlass);

const SearchCity = () => {

    const {getUsersLocation} = useWeatherService();
    if ("geolocation" in navigator) {
        console.log(navigator.geolocation.getCurrentPosition(function(position) {
            getUsersLocation(position.coords.latitude, position.coords.longitude)
                .then(city => console.log(city))

            console.log("Latitude is :", position.coords.latitude);
            console.log("Longitude is :", position.coords.longitude);
          }));
      } else {
        console.log("Not Available");
      }
    return (
        <div className="area">
            <div className="search-box">
                <FontAwesomeIcon icon={faLocationDot} />
                <input type="text" name="text" className="input" placeholder="Enter your location"/>
                <button type='submit'>
                    <FontAwesomeIcon icon={faMagnifyingGlass} />
                </button>
            </div>
        </div>
    )
}

export default SearchCity;
