import { useState, useEffect } from "react";
import useWeatherService from "../../services/WeatherService";
import Trip from "../trip/Trip";
import Weather from "../trip/Weather";

import './Carousel.css';

const IMG = [
  '../../assets/clear-day.svg',
  '../../assets/hail.svg',
  '../../assets/fog.svg'
]

const Carousel = ({ images }) => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const prevSlide = () => {
    const newIndex = (currentIndex - 1 + images.length) % images.length;
    setCurrentIndex(newIndex);
  };

  const nextSlide = () => {
    const newIndex = (currentIndex + 1) % images.length;
    setCurrentIndex(newIndex);
  };

  return (
    <div className="carousel">
      <img className="carousel-image" src={images[currentIndex]} alt={`Slide ${currentIndex + 1}`} />
      <button className="carousel-button prev" onClick={prevSlide}>
        Previous
      </button>
      <button className="carousel-button next" onClick={nextSlide}>
        Next
      </button>
    </div>
  );
};



function App() {
  const [trip, setTrip] = useState({ city: "Paris", date1: "2022-10-21", date2: "2022-10-28" });
  const [weekWeather, setWeekWeather] = useState([])
  const [weather, setWeather] = useState({});

  const { getForecast, getTodaysWeather } = useWeatherService();

  useEffect(() => {
    getTodaysWeather(trip.city)
      .then(item => {
        setWeather({ temperature: Math.round(item.days[0].temp), icon: item.days[0].icon })
      })
      .catch(e => console.log(e))

    getForecast(trip).then(item => {
      setWeekWeather(item.days)
    })
  }, [trip])

  return (
    <div className="App">
      <Trip weekWeather={weekWeather} setTrip={setTrip} />
      <Weather weather={weather} trip={trip} />
      <Carousel images={IMG} />
    </div>
  );
}

export default App;
