import './SearchCity.css'

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faLocationDot, faMagnifyingGlass } from '@fortawesome/free-solid-svg-icons';
import fontawesome from '@fortawesome/fontawesome'
import { useState } from 'react';

import useWeatherService from '../../services/WeatherService';

fontawesome.library.add(faLocationDot, faMagnifyingGlass);

const SearchCity = () => {

    const [location, setLocation] = useState({});
    const { getUsersLocation, getLocationByCity } = useWeatherService();

    const getLocation = () => {
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(
              (position) => {
                setLocation({
                  latitude: position.coords.latitude,
                  longitude: position.coords.longitude,
                });
              },
              (error) => {
                console.log(error);
              }
            );
          } else {
            console.log('Geolocation is not supported by this browser.');
          }

          if (location.latitude && location.longitude) {
            getUsersLocation(location.latitude, location.longitude)
              .then((response) => response.json())
              .then((data) => console.log(data.name))
              .catch((error) => console.log(error));
          }
    }

    return (
        <div className="area">
            <div className="search-box">
                <FontAwesomeIcon icon={faLocationDot} onClick={getLocation}/>
                <input type="text" name="text" className="input" placeholder="Enter your location"/>
                <button type='submit'>
                    <FontAwesomeIcon icon={faMagnifyingGlass} />
                </button>
            </div>
        </div>
    )
}

export default SearchCity;
