import React, { useState } from 'react';
import { AiOutlineClose } from 'react-icons/ai';
import './modal.css';

const Modal = (props) => {
  const { modal, setModal } = props;
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');

  const handleStartDateChange = (event) => {
    setStartDate(event.target.value);
    setEndDate(''); // Reset end date when start date changes
  };

  const handleEndDateChange = (event) => {
    setEndDate(event.target.value);
  };

  const today = new Date();
  const todayStr = today.toISOString().split('T')[0];

  const maxStartDate = new Date();
  maxStartDate.setDate(maxStartDate.getDate() + 15);
  const maxStartDateStr = maxStartDate.toISOString().split('T')[0];

  const minEndDate = startDate || undefined; // Set min to start date or undefined if not selected
  let maxEndDateStr = undefined; // Initialize maxEndDateStr as undefined

  if (startDate && !endDate) {
    const maxEndDate = new Date(startDate);
    maxEndDate.setDate(maxEndDate.getDate() + 15);
    maxEndDateStr = maxEndDate.toISOString().split('T')[0];
  }

  return (
    <div className="modal" style={{ visibility: modal }}>
      <div className="modal-wrapper">
        <div className="modal-heading mb-5">
          <h2>Create trip</h2>
          <button onClick={() => setModal('hidden')}>
            <AiOutlineClose size={20} />
          </button>
        </div>
        <div className="modal-trip mb-5">
          <form action="">
            <div className="mb-3">
              <label className="form-label">City</label>
              <input
                type="city"
                className="form-control"
                name=""
                placeholder="Please select a city"
              />
            </div>
            <div className="mb-3">
              <label className="form-label">Start date</label>
              <input
                type="date"
                className="form-control"
                name=""
                placeholder="Select date"
                onChange={handleStartDateChange}
                min={todayStr} // Set min to today's date
                max={maxStartDateStr}
              />
            </div>
            <div className="mb-3">
              <label className="form-label">End date</label>
              <input
                type="date"
                className="form-control"
                name=""
                placeholder="Select date"
                onChange={handleEndDateChange}
                min={minEndDate}
                max={maxEndDateStr}
                disabled={!startDate}
              />
            </div>
          </form>
        </div>
        <div className="modal-bottom gap-30">
          <button className="button-cancel">Cancel</button>
          <button className="button-save">Save</button>
        </div>
      </div>
    </div>
  );
};

export default Modal;
