import { useState, useEffect } from "react";
import useWeatherService from "../../services/WeatherService";
import Trip from "../trip/Trip";
import Weather from "../trip/Weather";

import './Carousel.css';

const IMG = [
  '../../assets/clear-day.svg',
  '../../assets/hail.svg',
  '../../assets/fog.svg'
]

const Carousel = ({ images }) => {
  const [activeIndex, setActiveIndex] = useState(0);

  const handleNext = () => {
    setActiveIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  const handlePrev = () => {
    setActiveIndex((prevIndex) => (prevIndex - 1 + images.length) % images.length);
  };

  const currentTransform = -activeIndex * 100;

  return (
    <div className="carousel-wrapper">
      <div
        className="carousel-items"
        style={{ transform: `translateX(${currentTransform}%)` }}
      >
        {images.map((image, index) => (
          <div key={index} className="carousel-item">
            <img src={image} alt={item} />
          </div>
        ))}
      </div>

      {/* Carousel controls will go here */}

      <button className="carousel-control prev" onClick={handlePrev}>
        Previous
      </button>
      <button className="carousel-control next" onClick={handleNext}>
        Next
      </button>
    </div>
  );
};


function App() {
  const [trip, setTrip] = useState({ city: "Paris", date1: "2022-10-21", date2: "2022-10-28" });
  const [weekWeather, setWeekWeather] = useState([])
  const [weather, setWeather] = useState({});

  const { getForecast, getTodaysWeather } = useWeatherService();

  useEffect(() => {
    getTodaysWeather(trip.city)
      .then(item => {
        setWeather({ temperature: Math.round(item.days[0].temp), icon: item.days[0].icon })
      })
      .catch(e => console.log(e))

    getForecast(trip).then(item => {
      setWeekWeather(item.days)
    })
  }, [trip])

  return (
    <div className="App">
      <Trip weekWeather={weekWeather} setTrip={setTrip} />
      <Weather weather={weather} trip={trip} />
      <Carousel images={IMG} />
    </div>
  );
}

export default App;
