import { useState } from 'react';
import './Carousel.css';

const IMG = [
    'https://images.pexels.com/photos/268533/pexels-photo-268533.jpeg?cs=srgb&dl=pexels-pixabay-268533.jpg&fm=jpg',
    'https://cdn.pixabay.com/photo/2014/02/27/16/10/flowers-276014_640.jpg',
    'https://images.unsplash.com/photo-1495344517868-8ebaf0a2044a?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxzZWFyY2h8M3x8c2VhcmNofGVufDB8fDB8fHww&w=1000&q=80',
    'https://www.befunky.com/images/prismic/1f427434-7ca0-46b2-b5d1-7d31843859b6_funky-focus-red-flower-field-after.jpeg?auto=avif,webp&format=jpg&width=863',
    'https://w.forfun.com/fetch/67/674d9f64c8a3c0110654ebdd1e037503.jpeg'
]

const Carousel = ({ images }) => {
    const [activeIndex, setActiveIndex] = useState(0);

    const handleNext = () => {
        setActiveIndex((prevIndex) => (prevIndex + 1) % images.length);
    };

    const handlePrev = () => {
        setActiveIndex((prevIndex) => (prevIndex - 1 + images.length) % images.length);
    };

    const currentTransform = -activeIndex * (100 / images.length);

    const isPrevDisabled = activeIndex === 0;
    const isNextDisabled = activeIndex === images.length - 1;

    return (
        <div className="carousel-wrapper">
            <div
                className="carousel-items"
                style={{ transform: `translateX(${currentTransform}%)` }}
            >
                {images.map((image, index) => (
                    <div key={index} className="carousel-item">
                        <img src={image} alt={'item'} />
                    </div>
                ))}
            </div>

            <button
                className={`carousel-control prev ${isPrevDisabled ? 'disabled' : ''}`}
                onClick={handlePrev}
                disabled={isPrevDisabled}
            >
                Previous
            </button>
            <button
                className={`carousel-control next ${isNextDisabled ? 'disabled' : ''}`}
                onClick={handleNext}
                disabled={isNextDisabled}
            >
                Next
            </button>
        </div>
    );
};

export default Carousel;
