import './SearchCity.css'

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faLocationDot, faMagnifyingGlass } from '@fortawesome/free-solid-svg-icons';
import fontawesome from '@fortawesome/fontawesome'

import { useState } from 'react';
import useWeatherService from '../../services/WeatherService';

fontawesome.library.add(faLocationDot, faMagnifyingGlass);

const SearchCity = (props) => {

    const {onLocationClick, city, setCity, location, onSearchCity} = props;
    const {googleAutocomplete} = useWeatherService();
    const [hints, setHints] = useState({});

    const renderHint = (input) => {
        googleAutocomplete(input)
            .then(({place_id, place}) =>  (
                setHints({place_id, place})
            ))

        return hints
    }

    return (
        <div className="area">
            <div className="search-box">
                <FontAwesomeIcon icon={faLocationDot} onClick={() => onLocationClick(location)}/>

                <input 
                    type="text" 
                    name="text" 
                    className="input" 
                    placeholder="Enter your location" 
                    value={city}
                    onChange={e => {
                        setCity(e.target.value);
                        renderHint(e.target.value)
                    } } ></input>
                <button 
                    type='submit'
                    onClick={() => onSearchCity(city)}>
                    <FontAwesomeIcon icon={faMagnifyingGlass}/>
                </button>

            </div>
        </div>
    )
}


// const View = (city, data, getLocation) => {
//     const {humidity, icon, precipitation, temperature, weather, wind} = data;

//     return (

//     )
// }
export default SearchCity;
