import { useHttp } from "../hooks/http.hook";


const useWeatherService = () => {
    const {request} = useHttp();
    const __apiBase = "http://dataservice.accuweather.com/"
    const __apiKey = 'Y7VaNReVLSjX0SIAu7ecTrwS2AX6siRU'

    const getUsersLocation = async (latitude, longitude) => {
        const res = await request(`${__apiBase}locations/v1/cities/geoposition/search?apikey=${__apiKey}`)
        return res["EnglishName"]
    }

    return {
        getUsersLocation
    }
}

export default useWeatherService;
