import { useHttp } from "../hooks/http.hook";

const useWeatherService = () => {
    const {request} = useHttp();
    const __apiKey = 'EUHRQXCFA9JUS3FDZQDZJTYQW'

    const getForecast = async (city, date1, date2) => {
        const res = await request(`https://weather.visualcrossing.com/VisualCrossingWebServices/rest/services/timeline/${city}/${date1}/${date2}?unitGroup=metric&include=days&key=${__apiKey}&contentType=json`)
        return { city: res.city.name, weather: res.list.map(_transformLocation)}
    }

    const getLocationByCity = async (city) => {
        const res = await request(`https://weather.visualcrossing.com/VisualCrossingWebServices/rest/services/timelin
e/[city]/today?unitGroup=metric&include=days&key=YOUR_API_KEY&contentType=
json`)
        return { "lon": res.coord.lon, 'lat': res.coord.lat}
    }

    const _transformLocation = (item) => {
        return {
            weather: item.weather[0].main,
            temperature: Math.floor(item.main.temp - 273.15),
            precipitation: item.pop,
            humidity: item.main.humidity,
            wind: item.wind.speed,
            icon: item.weather[0].icon
        }
    }

    const _transformPlaces = (item) => {
        return {
            place_id: item.place_id,
            place: item.structured_formatting.main_text
        }
    }

    return {
        getForecast,
        getLocationByCity
    }
}

export default useWeatherService;
